import React, { useState, useEffect } from 'react';

const TodoItem = (props) => {
    const [todoItem, setTodoItems] = useState(props.data);

    useEffect(() => {
        console.log("todo item changed", todoItem);
    }, [todoItem]);

    function updateIsDone() {
        setTodoItems({...todoItem, isDone: !todoItem.isDone});
    }

    return (
        <>
          <input type="checkbox" checked={todoItem.isDone} onChange={updateIsDone}/>
          <span>{todoItem.task}</span>
        </>
      );
};

export default TodoItem;
