import React, { useState, useEffect } from 'react';

const TodoItem = (props) => {
    const [todoItem, setTodoItem] = useState(props.data);
    const [isDirty, setDirty] = useState(false);

    useEffect(() => {
        if(isDirty) {
            fetch(`http://localhost:8080/api/todoItems/${todoItem.id}`, {
                method : 'PUT',
                headers : {
                    'content-type' : 'application/json'
                },
                body : JSON.stringify(todoItem)
            })
            .then(response => response.json())
            .then(data => {
                setDirty(false);
                setTodoItem(data);
            });

            console.log("todo item changed", todoItem);
        }
    }, [todoItem, isDirty]);

    function updateIsDone() {
        setDirty(true);
        setTodoItem({...todoItem, isDone: !todoItem.isDone});
    }

    function updateTask() {
        setTodoItem({...todoItem, task: "something"});
    }

    return (
        <div>
          <input type="checkbox" checked={todoItem.isDone} onChange={updateIsDone}/>
          <input type="text" value={todoItem.task} onChange={updateTask}/>
        </div>
      );
};

export default TodoItem;
